	public void printAllGenes(String dna) {
		int geneCount = 0;
		int currIndex = 0;
		while(!dna.isEmpty()) {
			String dnaReturn = findGene(dna);
			if(dnaReturn == "") {
				break;
			}
			else {
				System.out.println(dnaReturn);//print found dna
				geneCount += 1;
				int tempIndex = dna.indexOf(dnaReturn);
				currIndex = tempIndex + dnaReturn.length() + 3; //find new currindex
				dna = dna.substring(currIndex);
			}
		}
		System.out.println("Amount of genes in dna-string: " + geneCount);
	}
	
