	public void testFindMinIndex() {
		System.out.println(findMinIndex(5, 7, 9) + " result = 5"); 
		System.out.println(findMinIndex(99, 7, 5) + " result = 5"); 
		System.out.println(findMinIndex(-1, 7, 9) + " result = 7"); 
		System.out.println(findMinIndex(-1, 7, -1) + " result = 7");
		System.out.println(findMinIndex(-1, -1, 7) + " result = 7");
		System.out.println(findMinIndex(-1, -1, -1) + " result = -1");
	}
