	/**
	 * getAllGenes reads a string dna and returns an ArrayList<String>
	 * which contains all genes
	 * @param dna
	 * @return StorageResource genes
	 */
	public StorageResource getAllGenes(String dna) {
		StorageResource genes = new StorageResource();
		int geneCount = 0;
		int currIndex = 0;
		while(!dna.isEmpty()) {
			String dnaReturn = findGene(dna);
			if(dnaReturn == "") {
				break;
			}
			else {
				genes.add(dnaReturn);
				//System.out.println(dnaReturn);//print found dna
				geneCount += 1;
				int tempIndex = dna.indexOf(dnaReturn);
				currIndex = tempIndex + dnaReturn.length() + 3; //find new currindex
				dna = dna.substring(currIndex);
			}
		}
		System.out.println("Amount of genes in dna-string: " + geneCount);
		return genes;
	}
	
