	public String findGene(String dna) {
		int startIndex = dna.indexOf("ATG"); //find index of first occurence of ATG
		//System.out.println("StartCodon ATG gevonden, index = " + startIndex); //TODO delete after testing
		if(startIndex == -1) {//If ATG is not found
			return ""; //"No valid ATG found"; //return empty string
		}
		int taaIndex = findStopCodon(dna, startIndex, "TAA");
		if(taaIndex != -1) {
			//System.out.println("TAA gevonden, index is " + taaIndex); //TODO delete after testing
		}
		int tagIndex = findStopCodon(dna, startIndex, "TAG");
		if(tagIndex != -1) {
			//System.out.println("TAG gevonden, index is " + tagIndex);//TODO delete after testing
		}
		int tgaIndex = findStopCodon(dna, startIndex, "TGA");
		if(tgaIndex != -1) {
			//System.out.println("TGA gevonden, index is " + tgaIndex);//TODO delete after testing
		}
		int minIndex = findMinIndex(taaIndex, tagIndex, tgaIndex);// find the smallest of the three indices
		if(minIndex == -1) {
			return ""; //Geen Stopcodon gevonden
		}
		String gene = dna.substring(startIndex, minIndex+3);
		//System.out.println("Gene gevonden, " + gene); //TODO delete after testing
		return gene; 
	}
	
	/**
	 * Function to find the smallest number or return -1 if no StopCodon is found
	 * @param taaIndex
	 * @param tagIndex
	 * @param tgaIndex
	 * @return smallest index.
	 */
	public int findMinIndex(int taaIndex, int tagIndex, int tgaIndex) {
		int tempIndex = 0;
		if(taaIndex != -1) {//taaIndex bestaat
			if(tagIndex != -1) {//tagIndex bestaat
				if (taaIndex < tagIndex) {//taa < tag
					tempIndex = taaIndex; //temp = taa
				}
				else { //taa >= tag
					tempIndex = tagIndex; //temp = tag
				}
			} else { //tag bestaat niet
				tempIndex = taaIndex;
			}
		} 
		else { //taaIndex bestaat niet
			tempIndex = tagIndex;//
		}
		if(tgaIndex == -1 && tempIndex == -1) { //tga bestaat niet en geen waarde voor temp
			tempIndex = -1; //geen stopCodons gevonden
		}
		else {
			if(tgaIndex != -1) {// tga bestaat
				if(tempIndex == -1) { // nog geen andere stopCodon
					tempIndex = tgaIndex;
				}
				else {
					if(tgaIndex < tempIndex) {// tga kleiner dan tempIndex
						tempIndex = tgaIndex;
					}
				}
			}
		}
	return tempIndex;
	}
	
	/**
	 * This method returns the index of the first occurrence of stopCodon,
	 * which is past startIndex and a multiple of 3 away from startIndex
	 * If there is no stopCodon it returns the length of dna
	 * @param dna
	 * @param startIndex
	 * @param stopCodon
	 * @return
	 */
		public int findStopCodon(String dna, int startIndex, String stopCodon) {
			int findIndex = dna.indexOf(stopCodon, startIndex+3);
			while(findIndex != -1) {
				
				//System.out.println("only for test purpose! findIndex = " + findIndex);
				if((findIndex - startIndex) % 3 == 0) {
					return findIndex;
				}
				else {
					findIndex = dna.indexOf(stopCodon, findIndex + 1);
				}
			}
			return -1;//return dna.length(); //old version, changed into returning -1
		}
	
		public float cgRatio(String dna) {
			int dnaLength = dna.length();
			int amountC = 0;
			int amountG = 0;
			if(dnaLength == 0) {
				return (float)0.0;
			}
			int index = 0;
			while (index < dnaLength) {
				String temp = dna.substring(index, index+1);
				if (temp.compareToIgnoreCase("C") == 0) {
					amountC +=1;
					
				}
				if(temp.compareToIgnoreCase("G") == 0) {
					amountG += 1;
				}
				index += 1;
			}
			// calculate ratio amoutC + amountG / dnaLenght
			//System.out.println("Amount C: " + amountC + ", Amount G: " + amountG + ", String lenght " + dnaLength);
			return (float)(amountC + amountG)/ dnaLength;
		}
		
