		public void processGenes (StorageResource sr) {

			StorageResource srLong = new StorageResource();
			StorageResource srCG = new StorageResource();
			for(String gene : sr.data()) {//until EOF, read gene
				if(gene.length()> 60) {
					srLong.add(gene); //add gene > 60 to new SR
					//System.out.println(gene);//TODO delete after test print gene longer than 9 chars
				}
				float cgRatio = this.cgRatio(gene);
				if(cgRatio > 0.35) {
					srCG.add(gene); //add gene CG-ration > 0.35
				}
			}// End of for loop 
			this.processLong(srLong);
			this.processCGRatio(srCG);
		}
		public void processLong(StorageResource sr) {
			int longestGene = 0;
			int amountLongGene = 0;
			System.out.println("Printing genes > 60 characters");
			for(String gene : sr.data()) {
				if(gene.length() > longestGene) {//find longest gene
					longestGene = gene.length();
				}
				System.out.println(gene);//print number of strings > 9 chars
				amountLongGene += 1;
			}
			System.out.println("Nr of strings > 60: " + amountLongGene);
			System.out.println("Nr of strings in Array: " + sr.size());
			System.out.println("longest gene = " + longestGene);
		}
		
		public void processCGRatio(StorageResource sr) {
			System.out.println("Printing genes with C-G ration > 0.35");
			for(String gene : sr.data()) {//print gene where CG-ration > 0.35
			//print number of genes with CG-ratio > .35
				System.out.println(gene);
			}
			System.out.println("Nr of genes with C-G ratio > 0.35: " + sr.size());
		}
		
